import React, { Component } from 'react';

import Navigation from "./Navigation";
import LearnPage from './LearnPage';
import UnderstandPage from './UnderstandPage';
import ReviewPage from './ReviewPage';
import IntroBody from './Landing_Page/IntroBody';

export default class LandingPage extends Component {
  constructor () {
    super ();
    this.state= {
      questReview: [],
      page: [
        "landing", 
        "learn", 
        "quiz", 
        "review"
      ]
    }
  }

  handleLearn() {
    return(
      <LearnPage />
    )
  }

  handleQuiz() {
    return (
      <UnderstandPage />
    )
  }

  handleReview() {
    return (
      <ReviewPage />
    )
  }

  handleNavClick = (event) => {
    let btnClicked = this.buttonClass(event.target.className, btn);
    console.log("You are clicking:", btnClicked)
  }

  buttonClass = (btn, btnClicked, toRender) => {
    if (btn.includes("home")) {
      btnClicked = "You clicked: home"
      toRender = <IntroBody navClick={this.handleNavClick}/>
    } else if (btn.includes("learn")){
      btnClicked = "You clicked: learn"
    } else if (btn.includes("quiz")) {
      btnClicked = "You clicked: quiz"
    } else if (btn.includes("review")) {
      btnClicked = "You clicked: review"
    } else {
      console.log("Alert: Nothing is happening!")
    }
    return (btnClicked, toRender);
  }

  render () {
    let toRender;
    return(
      <div className="layout">
        <Navigation navClick={this.handleNavClick}/>
        <IntroBody navClick={this.handleNavClick}/>  
      </div>
    )
  }
}
