import React, { Component } from 'react';

import Navigation from "./Navigation";
import LearnPage from './LearnPage';
import UnderstandPage from './UnderstandPage';
import ReviewPage from './ReviewPage';
import IntroBody from './Landing_Page/IntroBody';

export default class LandingPage extends Component {
  constructor () {
    super ();
    this.state= {
      questReview: []
    }
  }

  handleLearn() {
    return(
      <LearnPage />
    )
  }

  handleQuiz() {
    return (
      <UnderstandPage />
    )
  }

  handleReview() {
    return (
      <ReviewPage />
    )
  }

  handleNavClick(event) {
    console.log(event.target.className)
    this.buttonClass(event.target);
  }

  buttonClass(btn) {
    let home;
    let learn;
    let quiz;
    let review;
    if (btn.className == home) {
      home = btn;
      console.log("You clicked: home")
    } else if (btn.className.includes(learn) ) {
      learn = btn;
      console.log("You clicked: learn")
    } else if (btn.className == quiz) {
      quiz = btn;
      console.log("You clicked: quiz")
    } else if (btn.className == review) {
      review = btn;
      console.log("You clicked: review")
    } else {
      console.log("Alert: Nothing is happening!")
    }
    return home, learn, quiz, review;
  }

  render () {
    return(
      <div className="layout">
        <Navigation navClick={this.handleNavClick}/>
        <IntroBody navClick={this.handleNavClick}/>  
      </div>
    )
  }
}
