import React, { Component } from 'react';

import Navigation from "./Navigation";
import LearnPage from './LearnPage';
import UnderstandPage from './UnderstandPage';
import ReviewPage from './ReviewPage';
import IntroBody from './Landing_Page/IntroBody';

export default class LandingPage extends Component {
  constructor () {
    super ();
    this.state= {
      questReview: []
    }
  }

  handleLearn() {
    return(
      <LearnPage />
    )
  }

  handleQuiz() {
    return (
      <UnderstandPage />
    )
  }

  handleReview() {
    return (
      <ReviewPage />
    )
  }

  handleNavClick = (event) => {
    let home;
    let learn;
    let quiz;
    let review;
    console.log(event.target.className)
    this.buttonClass(event.target.className, home, learn, quiz, review);
    console.log(home)
  }

  buttonClass = (btn, home, learn, quiz, review) => {
    if (btn.includes("home")) {

      home = "You clicked: home"
      return home
    } else if (btn.includes("learn")){

      learn = "You clicked: learn"
      return leaern
    } else if (btn.includes("quiz")) {

      quiz = "You clicked: quiz"
    } else if (btn.includes("review")) {

      review = "You clicked: review"
    } else {
      console.log("Alert: Nothing is happening!")
    }
    return (home, learn, quiz, review);
  }

  render () {
    return(
      <div className="layout">
        <Navigation navClick={this.handleNavClick}/>
        <IntroBody navClick={this.handleNavClick}/>  
      </div>
    )
  }
}
